import { useSpectral } from '@spectral-finance/spectral-modal'
import React, { useState, useEffect } from 'react'
import { Box, Button, css, Grid, Skeleton, useTheme } from '@mui/material'
import { Background } from '../../../components/Background'
import { Score } from './Score'

export const ScoreSDK = () => {
  const { start, score } = useSpectral()
  const [myScore, setMyScore] = useState()

  const styles = {
    wrapper: css`
      position: relative;
      padding-bottom: 260px;
      max-width: 1307px;
      margin: 0 auto;
    `,
    poolWrapper: css`
      position: relative;
      margin-bottom: 40px;
      z-index: 1;
    `,
  }

  useEffect(() => {
    if (!myScore) {
      console.log('Score not calculated')
      return
    }
    console.log(`Hooray! your score is ${score}`)
    setMyScore(score)
  }, [score])

  return (
    <div>
      <button type='button' onClick={start}>
        Calculate Spectral Score
      </button>
    </div>
  )

  return (
    <Box css={styles.wrapper}>
      <Box css={styles.poolWrapper}><Score /></Box>
      <ScoreSDK />
      <Background />
    </Box>
  )
}
