","keccak256":"0xa1e8e83cd0087785df04ac79fb395d9f3684caeaf973d9e2c71caef723a3a5d6","license":"MIT"},"contracts/NFC.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.8.11;

import "./interfaces/INFC.sol";
import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import "@openzeppelin/contracts/utils/cryptography/SignatureChecker.sol";
import "@openzeppelin/contracts/utils/Strings.sol";

import "@openzeppelin/contracts-upgradeable/token/ERC721/ERC721Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/cryptography/draft-EIP712Upgradeable.sol";

struct NFCData {
    address[] accounts;
    string customUri;
}

contract NFC is ERC721Upgradeable, EIP712Upgradeable, OwnableUpgradeable, INFC {
    using ECDSA for bytes32;
    using Strings for uint256;

    mapping(uint256 => NFCData) private _lookup;
    mapping(address => uint256) private _reverseLookup;
    uint256 public counter;
    string internal __baseURI;

    function initialize() public initializer {
        __Ownable_init();
        __ERC721_init("Non Fungible Credit", "NFC");
        __EIP712_init("Spectral", "1");
        counter = 1;
    }

    function mint() public returns (uint256) {
        require(!isInNFC(msg.sender), "NFC_ALREADY_EXISTS");
        uint256 currentCounter = counter++;
        NFCData storage nfcData = _lookup[currentCounter];
        _reverseLookup[msg.sender] = currentCounter;
        nfcData.accounts.push(msg.sender);
        _mint(msg.sender, currentCounter);
        emit NFCCreated(currentCounter, nfcData.accounts);
        return currentCounter;
    }

    function addAccounts(address[] memory accounts, bytes[] memory signatures) public {
        require(accounts.length == signatures.length, "ARRAY_LENGTH_MISMATCH");
        uint256 id = getNFCId(msg.sender);
        NFCData storage nfcData = _lookup[id];
        for (uint256 i = 0; i < accounts.length; i++) {
            address account = accounts[i];
            require(!isInNFC(account), _errorMsg("ALREADY_IN_NFC", account));
            require(
                verify(account, _hash(), signatures[i]),
                _errorMsg("INVALID_SIGNATURE", account)
            );
            _reverseLookup[account] = id;
            nfcData.accounts.push(account);
        }
        emit AccountsAdded(id, accounts);
    }

    function mintAndAdd(
        address[] memory accounts,
        bytes[] memory signatures
    ) external returns (uint256 id) {
        id = mint();
        addAccounts(accounts, signatures);
    }

    function merge(uint256[] memory ids, bytes[] memory signatures) external {
        require(ids.length == signatures.length, "ARRAY_LENGTH_MISMATCH");
        uint256 mergeIntoId = getNFCId(msg.sender);
        NFCData storage nfcData = _lookup[mergeIntoId];
        for (uint256 i = 0; i < ids.length; i++) {
            uint256 id = ids[i];
            address signer = ECDSA.recover(_hash(), signatures[i]);
            require(_reverseLookup[signer] == id, _errorMsg("SIGNER_NOT_IN_NFC", id));
            address[] memory accounts = _lookup[id].accounts;
            for (uint256 j = 0; j < accounts.length; j++) {
                nfcData.accounts.push(accounts[j]);
                _reverseLookup[accounts[j]] = mergeIntoId;
            }
            // cannot delete storage variable https://ethereum.stackexchange.com/a/35993
            delete _lookup[ids[i]];
            _burn(ids[i]);
            emit NFCMerged(mergeIntoId, ids);
        }
    }

    function getAddresses(uint256 id) external view returns (address[] memory) {
        return _lookup[id].accounts;
    }

    function getNFCId(address account) public view returns (uint256) {
        uint256 id = _reverseLookup[account];
        require(id != 0, "NFC_NOT_FOUND");
        return id;
    }

    function isInNFC(address account) public view returns (bool) {
        return _reverseLookup[account] != 0;
    }

    function _errorMsg(string memory err, address account) private pure returns (string memory) {
        return string(abi.encodePacked(err, ": ", uint256(uint160(account)).toHexString(20)));
    }

    function _errorMsg(string memory err, uint256 id) private pure returns (string memory) {
        return string(abi.encodePacked(err, ": ", id.toString()));
    }

    function _beforeTokenTransfer(address from, address to, uint256) internal pure {
        // removed override
        require(from == address(0) || to == address(0), "NFC_NOT_TRANSFERABLE");
    }

    function _baseURI() internal view virtual override returns (string memory) {
        return __baseURI;
    }

    function setBaseURI(string calldata baseURI) external onlyOwner {
        __baseURI = baseURI;
        emit BaseURIUpdated(baseURI);
    }

    function setCustomURI(string calldata _customUri) external {
        uint256 tokenId = getNFCId(msg.sender);
        NFCData storage nfcData = _lookup[tokenId];
        nfcData.customUri = _customUri;
        emit CustomURIUpdated(tokenId, _customUri);
    }

    function tokenURI(uint256 tokenId) public view virtual override returns (string memory) {
        if (tokenId == 0) {
            return "";
        }
        string memory uri = _lookup[tokenId].customUri;
        if (bytes(uri).length != 0) {
            return uri;
        }
        return super.tokenURI(tokenId);
    }

    function _hash() internal view returns (bytes32) {
        string memory content = "This is Spectral Team. We are verifying ownership of the wallet.";
        return
            _hashTypedDataV4(
                keccak256(abi.encode(keccak256("Value(string content)"), keccak256(bytes(content))))
            );
    }

    function verification(bytes memory _signature) external view returns (address) {
        return ECDSA.recover(_hash(), _signature);
    }

    function verify(
        address _signer,
        bytes32 _digest,
        bytes memory _signature
    ) internal view returns (bool) {
        return ECDSA.recover(_digest, _signature) == _signer;
    }

    uint256[50] private __gap;
}
","keccak256":"0x1424b3b151bc74a68808d3d9edd511a96c837d342cc3a1f0b4be6f132f3c4070","license":"MIT"},"contracts/Vouch.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.8.11;

import "./NFC.sol";
import "./interfaces/IScoracle.sol";

error Vouch__VoucherNoNFC();
error Vouch__VoucheeNoNFC();
error Vouch__AlreadyVouchedForUser();
error Vouch__NotVouchedForUser();
error Vouch__TooManyVouches();
error Vouch__VoucherLower();
error Vouch__TooLowScore();
error Vouch__ClawbackFailed();
error Vouch__ReturnCollateralFailed();
error Vouch__YieldPaymentFailed();

contract Vouch {
    //===============Events===============

    event UserVouched(address indexed voucher, address indexed vouchee);
    event UserUnvouched(address indexed voucher, address indexed vouchee);
    event CollateralPosted(address indexed voucher, address indexed vouchee, uint indexed amount);
    event CollateralClawedBack(
        address indexed voucher,
        address indexed vouchee,
        uint indexed amount
    );
    event CollateralReturned(
        address indexed voucher,
        address indexed vouchee,
        uint amount,
        uint yield
    );
    event YieldPosted(address indexed voucher, address indexed vouchee, uint indexed amount);

    //===============State Variables===============

    address constant NFC_ADDRESS = 0x91e1156d5Fba6b1B251f396e96aFAaCE91394283; // Arbitrum Goerli Spectral
    address constant SCORACLE_ADDRESS = 0xe953f329041dA0D5Cf23159abc4b45f6fbf8Ab17; // Arbitrum Goerli Spectral
    bytes32 public SCORE_TYPE_ID = bytes32("9348f5ba3f574f65958a675e468da9c8");

    mapping(address => mapping(address => bool)) public vouchLookup; // voucher => (vouchee => true/false)
    mapping(address => mapping(address => bool)) public vouchReverseLookup; // vouchee => (voucher => true/false)
    mapping(address => mapping(address => uint)) public vouchCollateral;
    mapping(address => mapping(address => uint)) public vouchCollateralYield; // voucher => (vouchee => amount of yield)
    mapping(address => uint) public vouchCount; // voucher => number of vouches

    //===============Modifiers===============

    //===============Main Functions===============

    // Vouching requirements: credit score of voucher, credit score of vouchee,
    // number of people voucher has vouched for, proportion of the liquidity, average MACRO score
    //

    function vouch(address vouchee) public {
        // check that vouchee has not taken out loan
        NFC nfc = NFC(NFC_ADDRESS);
        IScoracle scoracle = IScoracle(SCORACLE_ADDRESS);
        // Voucher and vouchee must have each minted an NFC
        if (!nfc.isInNFC(msg.sender)) {
            revert Vouch__VoucherNoNFC();
        }
        if (!nfc.isInNFC(vouchee)) {
            revert Vouch__VoucheeNoNFC();
        }
        // Voucher must have higher MACRO score than vouchee
        // MACRO score getter function from Scoracle
        // IScoracle.ScoreData memory voucherScoreData = scoracle.getScore(msg.sender, SCORE_TYPE_ID);
        // uint voucherScore = voucherScoreData.score;
        // IScoracle.ScoreData memory voucheeScoreData = scoracle.getScore(vouchee, SCORE_TYPE_ID);
        // uint voucheeScore = voucheeScoreData.score;

        // Add in spoof logic
        uint voucherScore = 800;
        uint voucheeScore = 650;

        if (voucherScore < voucheeScore + 10) {
            revert Vouch__VoucherLower();
        }
        // Voucher must have a remaining "vouch"
        if (allowedVouchCount(voucherScore) - vouchCount[msg.sender] == 0) {
            revert Vouch__TooManyVouches();
        }
        if (vouchLookup[msg.sender][vouchee]) {
            revert Vouch__AlreadyVouchedForUser();
        }
        // If all requirements passed, link voucher and vouchee
        vouchLookup[msg.sender][vouchee] = true;
        vouchReverseLookup[vouchee][msg.sender] = true;
        vouchCount[msg.sender] += 1;
        emit UserVouched(msg.sender, vouchee);
    }

    function unvouch(address vouchee) public {
        // require msg.sender to have an NFC
        // require vouchee to have an NFC

        if (!vouchLookup[msg.sender][vouchee]) {
            revert Vouch__NotVouchedForUser();
        }

        vouchLookup[msg.sender][vouchee] = false;
        vouchReverseLookup[vouchee][msg.sender] = false;
        vouchCount[msg.sender] -= 1;
        emit UserUnvouched(msg.sender, vouchee);
    }

    /**
     * @dev This function decides whether the voucher can successfully make another vouch
     * @param score The MACRO score of the voucher
     */
    function allowedVouchCount(uint score) internal pure returns (uint total) {
        if (score < 650) {
            revert Vouch__TooLowScore();
        }
        if (650 <= score && score < 700) {
            total = 1;
        }
        if (700 <= score && score < 750) {
            total = 2;
        }
        if (750 <= score && score < 800) {
            total = 3;
        }
        if (800 <= score && score <= 850) {
            total = 4;
        }
    }

    function postCollateral(address vouchee, uint amount) public payable {
        if (!vouchLookup[msg.sender][vouchee]) {
            revert Vouch__NotVouchedForUser();
        }
        // require collateral posted = collateral required Underwriter.sol
        vouchCollateral[msg.sender][vouchee] = amount;
        emit CollateralPosted(msg.sender, vouchee, amount);
    }

    function postYield(address voucher, address vouchee, uint amount) public payable {
        // require msg.sender = pool address
        if (!vouchLookup[voucher][vouchee]) {
            revert Vouch__NotVouchedForUser();
        }
        vouchCollateralYield[voucher][vouchee] = amount;
        emit YieldPosted(voucher, vouchee, amount);
    }

    function returnCollateral(address voucher, address vouchee) public {
        // require msg.sender == LiquidityPool contract address
        uint amount = vouchCollateral[voucher][vouchee];
        (bool s, ) = payable(voucher).call{value: amount}("");
        if (!s) {
            revert Vouch__ReturnCollateralFailed();
        }
        // request and pay back voucher the interest gained
        uint yield = vouchCollateralYield[voucher][vouchee];
        (bool success, ) = payable(voucher).call{value: yield}("");
        if (!success) {
            revert Vouch__YieldPaymentFailed();
        }
        // update data structures
        vouchCollateral[voucher][vouchee] = 0;
        vouchCollateralYield[voucher][vouchee] = 0;
        // INCREASE REPUTATION SCORE
        // Unvouch for user
        vouchLookup[msg.sender][vouchee] = false;
        vouchReverseLookup[vouchee][msg.sender] = false;
        vouchCount[msg.sender] -= 1;

        emit UserUnvouched(voucher, vouchee);
        emit CollateralReturned(voucher, vouchee, amount, yield);
    }

    function clawback(address voucher, address vouchee) public {
        // require msg.sender == LiquidityPool contract address
        uint amount = vouchCollateral[voucher][vouchee];
        (bool s, ) = payable(msg.sender).call{value: amount}("");
        if (!s) {
            revert Vouch__ClawbackFailed();
        }
        vouchCollateral[voucher][vouchee] = 0;
        // Unvouch for user
        vouchLookup[msg.sender][vouchee] = false;
        vouchReverseLookup[vouchee][msg.sender] = false;
        vouchCount[msg.sender] -= 1;

        emit UserUnvouched(voucher, vouchee);
        emit CollateralClawedBack(voucher, vouchee, amount);
    }
}
","keccak256":"0x793db28e1d5ca90c1ecbd63829dd1996a19b1812e51ef7f5e06cbf2cfabd024d","license":"MIT"},"contracts/interfaces/INFC.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.8.11;

interface INFC {
    event NFCCreated(uint256 indexed id, address[] accounts);
    event AccountsAdded(uint256 indexed id, address[] accounts);
    event NFCMerged(uint256 indexed id, uint256[] ids);
    event BaseURIUpdated(string uri);
    event CustomURIUpdated(uint256 indexed id, string uri);

    function mint() external returns (uint256);

    function addAccounts(address[] memory accounts, bytes[] memory signatures) external;

    function mintAndAdd(
        address[] memory accounts,
        bytes[] memory signatures
    ) external returns (uint256 id);

    function merge(uint256[] memory ids, bytes[] memory signatures) external;

    function getAddresses(uint256 id) external view returns (address[] memory);

    function getNFCId(address account) external view returns (uint256);
}
","keccak256":"0xc9a985eb330e8b4347b570dea2c21fbb7d020f77ee71299c20587332eedf8f7d","license":"MIT"},"contracts/interfaces/IScoracle.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.8.11;

interface IScoracle {
    struct ScoreData {
        uint40 lastUpdated;
        uint216 score;
        bytes extraData;
    }

    //===============Events===============

    /**
     * @dev emitted upon callback function of chainlink call. emits the address that was updated, the time it was updatedand the tick that was returned
     * @param addressToScore The address whose score is being updated
     * @param lastUpdated Timestamp of last update
     * @param score The new score
     * @param extraData Extra data to the type of score request
     **/
    event ScoreUpdated(
        address indexed addressToScore,
        uint256 lastUpdated,
        uint256 score,
        bytes extraData
    );

    /**
     * @dev Added a new score type
     * @param scoreTypeJobId The new adapter job id
     * @param scoreTypeName The new score type name
     **/
    event ScoreTypeAdded(bytes32 indexed scoreTypeJobId, string scoreTypeName);

    /**
     * @dev Deactivated a score type
     * @param scoreTypeJobId The new adapter job id
     **/
    event ScoreTypeDeactivated(bytes32 indexed scoreTypeJobId);

    /**
     * @dev Updated the chainlink node address
     * @param chainlinkNode The new chainlink node address
     **/
    event ChainlinkNodeUpdated(address indexed chainlinkNode);

    /**
     * @dev Updated the chainlink oracle address
     * @param chainlinkOracle The new chainlink oracle address
     **/
    event ChainlinkOracleUpdated(address indexed chainlinkOracle);

    /**
     * @dev Updated the base fee
     * @param baseFee Base fee updated
     **/
    event BaseFeeUpdated(uint256 baseFee);

    /**
     * @dev Updated the chainlink fee
     * @param chainlinkFee The new chainlink fee
     **/
    event ChainlinkFeeUpdated(uint256 chainlinkFee);

    //===============Main Functions===============

    function scoreRequest(
        address addressToScore,
        bytes32 _scoreTypeJobId,
        bytes memory _userSignature
    ) external payable;

    //===============Governance/Admin Functions===============

    function addScoreType(bytes32 _scoreTypeJobId, string memory _scoreTypeName) external;

    function deactivateScoreType(bytes32 _scoreTypeJobId) external;

    function updateChainlinkNode(address chainlinkNode) external;

    function updateChainlinkOracle(address chainlinkOracle) external;

    function updateBaseFee(uint256 baseFee) external;

    function updateChainlinkFee(uint256 chainlinkFee) external;

    function depositLINK(uint256 amount) external;

    function withdrawLINK(uint256 amount) external;

    function withdrawETH(uint256 amount) external;

    //===============Get Functions===============

    function getScore(
        address _user,
        bytes32 _scoreTypeJobId
    ) external view returns (ScoreData memory scoreData);

    function checkScoreTypeExists(
        bytes32 _scoreTypeJobId
    ) external view returns (bool, string memory, bytes32);

    function getChainlinkNode() external view returns (address);

    function getChainlinkOracle() external view returns (address);

    function getBaseFee() external view returns (uint256);

    function getChainlinkFee() external view returns (uint256);

    function getScoreBounds() external view returns (uint256, uint256);
}
